package Tmf_Assignment01;
public class Typecasting_program {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        //converting 'int' value into double
		int num=10;
		double d=num;
		System.out.println("The converted value is:"+d);
		//converting "double" into int
		double num1=10.00;
		int i=(int)num1;
		System.out.println("The converted value is:"+i);
	}

}
